package loops;

public class EvenOdd10Numbers {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i=1;i<=20; i++){
			if(i%2==0) {
			System.out.println("even number"+i);
			}
//			else{
//				System.out.println("odd number"+ i);
//			}
		}
		
		
		for (int i=1;i<=20; i++){
			if(i%2!=0) {
			System.out.println("odd number"+i);
			}

	
		
	}

}}